import React from 'react';
import './ListItems.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import FlipMove from 'react-flip-move';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';


function ListItems(props){
    const items = props.items;
    const completedItems = this.completedItems;
    const activeItems = this.activeItems;
    const listItems = items.map(item =>
        
   { 
       var itemClass = " " + ( item.status ? "done" : "undone");
        console.log(itemClass)
       return <div className="list" key={item.key}>
     <p>
     <CheckCircleIcon className={"faicon"} onClick={() => {
            props.completeItem(item.key)
        }} icon="trash" />
        
         <input type="text" className={itemClass} id={item.key} value={item.text} onChange={(e)=>{
             props.setUpdate(e.target.value,item.key)}}/>
        <span>
       
        <FontAwesomeIcon className="faicons" onClick={() => {
            props.deleteItem(item.key)
        }} icon="trash" />
        </span>
     </p>
     
    </div>})
    
    if (this.props.whatToView === ''){
        return (
            <div>
             <FlipMove duration={300} easing="ease-in-out">
             {listItems}
             </FlipMove>
         </div>)    
    }
    if(this.props.whatToView === 'active')
    return (
        <div>
         <FlipMove duration={300} easing="ease-in-out">
         {activeItems}
         </FlipMove>
     </div>) ;
    if ( this.props.whatToView === 'completed'){
        return (
            <div>
             <FlipMove duration={300} easing="ease-in-out">
             {completedItems}
             </FlipMove>
         </div>) 
    }

  }

  export default ListItems;
